def home_page(request):
    template_name = 'quotes/home.html'  
    context = {
        'dennis_hopper_image': random.choice(dennis_hopper_images),
        'dennis_hopper_quote': random.choice(dennis_hopper_quotes),
    }
    
    return render(request, template_name, context)

def about(request):
    template_name = 'quotes/about.html'
    context = {
        'dennis_hopper_image': random.choice(dennis_hopper_images),
        'dennis_hopper_quote': random.choice(dennis_hopper_quotes)
    }
    return render(request, template_name, context)

def show_all(request):
    template_name = 'quotes/show_all.html'
    context = {
        'all_images': dennis_hopper_images,
        'all_quotes': dennis_hopper_quotes
    }
    return render(request, template_name, context)